import React from "react";

import {Container} from "semantic-ui-react";

import {UserDetails} from "./UserDetails";
import {PersonalDetails} from "./PersonalDetails";
import {About} from "./About";
import {Success} from "./Success";

import {Form} from "semantic-ui-react";
import UserForm from "../user-component/UserForm";
import FeedbackForm from "../feedback-component/FeedbackForm";

handleSubmit = (e) => {
  e.preventDefault();
  //just show the success page (step 4 )
  this.setState((prevState) => {
    return {
      ...prevState,
      step: 4,
    };
  });
};

handleChange = (event, {name, value, checked, type}) => {
  if (type === "checkbox") {
    //TODO generic solution for these checkboxes!!
    let {interests} = this.state;
    if (checked) {
      interests = [...interests, value];
    } else {
      interests = interests.filter((item) => item !== value);
    }
    this.setState((prevState) => {
      return {
        ...prevState,
        interests: interests,
      };
    });
  } else {
    this.setState((prevState) => {
      return {
        ...prevState,
        [name]: value,
      };
    });
  }
};

next = () => {
  this.setState((prevState) => {
    return {
      ...prevState,
      step: prevState.step + 1,
    };
  });
};

prev = () => {
  this.setState((prevState) => {
    return {
      ...prevState,
      step: prevState.step - 1,
    };
  });
};

function MainFormContainer() {
  state = {
    step: 1,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    age: "",
    country: "",
    interests: [],
    about: "",
  };

  const {step} = this.state;
  const {firstName, lastName, email, gender, age, country, interests, about} =
    this.state;
  const values = {
    firstName,
    lastName,
    email,
    gender,
    age,
    country,
    interests,
    about,
  };

  return (
    <Container textAlign='left'>
      <Form onSubmit={this.handleSubmit}>
        <Step
          step={step}
          values={values}
          handleChange={this.handleChange}
          next={this.next}
          prev={this.prev}
        />
      </Form>
    </Container>
  );
}

const Step = ({step, values, handleChange, next, prev}) => {
  switch (step) {
    case 1:
      return (
        <UserForm values={values} handleChange={handleChange} next={next} />
      );
    case 2:
      return (
        <FeedbackForm
          values={values}
          handleChange={handleChange}
          next={next}
          prev={prev}
        />
      );
    case 4:
    // return <Success values={values}
    // />
    default:
      return null;
  }
};

export default MainFormContainer
