import React, {useEffect, useState} from "react";
import {Card, Container, Search} from "semantic-ui-react";
import API from "../../api";
import "./style.css";
async function fetchDataCall() {
  try {
    const response = await API.get("/feedbacks");
    return response;
  } catch (error) {
    console.log("error", error);
  }
}

async function getSearchResults(searchValue) {
  try {
    const response = await API.get(`/feedbacks?s=${searchValue}`);
    return response;
  } catch (error) {
    console.log("error in seach", error);
  }
}

function FeedbackList() {
  const [data, setData] = useState([]);
  const [values, setValues] = useState({
    isLoading: false,
    results: [],
    searchValue: "",
  });

  useEffect(() => {
    const getData = async () => {
      let data = await fetchDataCall();
      setData(data);
    };
    getData();
  }, []);

  useEffect(() => {
    const filterData = async (searchValue) => {
      let data = await getSearchResults(searchValue);
      setValues({results: data});
    };
    filterData();
  }, [values.searchValue, data]);

  const handleSeach = (event, {value}) => {
    setValues({searchValue: value});
    getSearchResults(value);
  };

  let FeedbackListToRender;
  const {searchValue, results, isLoading} = values;

  if (results && results !== [] &&  results.data !== undefined) {

    console.log('filtered', results.data)
    FeedbackListToRender = results.data.data.map((feedback) => (
      <Card fluid>
        <Card.Content>
          <Card.Header>{feedback.attributes.body}</Card.Header>
          <Card.Description>
            <p>
              <b>User:</b> {feedback.attributes.user.firstname}{" "}
              {feedback.attributes.user.lastname}
            </p>
            <p>
              <b>Email:</b> {feedback.attributes.user.email}{" "}
            </p>
          </Card.Description>
        </Card.Content>
      </Card>
    ));
  }

  return (
    <Container className='main-container'>
      <Search
        className='search-input'
        input={{icon: "search", iconPosition: "left"}}
        loading={isLoading}
        // onResultSelect={handleResultSelect}
        onSearchChange={handleSeach}
        results={results && results.data && results.data.data}
        value={searchValue}
      />
      <Card.Group>{FeedbackListToRender}</Card.Group>
    </Container>
  );
}

export default FeedbackList;
