import React, {useEffect, useState} from "react";
import {Card, Container, Search} from "semantic-ui-react";
import API from "../../api";
import "./style.css";
async function fetchDataCall() {
  try {
    const response = await API.get("/feedbacks");
    return response;
  } catch (error) {
    console.log("error", error);
  }
}

async function getSearchfilteredFeedbacks(searchValue) {
  try {
    const response = await API.get(`/feedbacks?q=${searchValue}`);
    return response;
  } catch (error) {
    console.log("error in seach", error);
  }
}

function FeedbackList() {
  const [data, setData] = useState([]);
  const [values, setValues] = useState({
    isLoading: false,
    filteredFeedbacks: [],
    searchValue: "",
  });

  useEffect(() => {
    const getData = async () => {
      let data = await fetchDataCall();
      setData(data);
    };
    getData();
  }, []);

  useEffect(() => {
    const filterData = async (searchValue) => {
      let data = await getSearchfilteredFeedbacks(searchValue);
      setValues({filteredFeedbacks: data});
    };
    filterData();
  }, [values.searchValue, data]);

  const handleSeach = (event, {value}) => {
    setValues({searchValue: value});
    getSearchfilteredFeedbacks(value);
  };

  const {searchValue, filteredFeedbacks, isLoading} = values;

  let FeedbackListToRender;
  if (filteredFeedbacks && filteredFeedbacks.data !== undefined) {
    console.log("filtered", filteredFeedbacks.data);
    FeedbackListToRender = filteredFeedbacks.data.data.map((feedback) => (
      <Card fluid>
        <Card.Content>
          <Card.Header>{feedback.attributes.body}</Card.Header>
          <Card.Description>
            <p>
              <b>User:</b> {feedback.attributes.user.firstname}{" "}
              {feedback.attributes.user.lastname}
            </p>
            <p>
              <b>Email:</b> {feedback.attributes.user.email}{" "}
            </p>
          </Card.Description>
        </Card.Content>
      </Card>
    ));
  }

  return (
    <Container className='main-container'>
      <Search
        className='search-input'
        input={{icon: "search", iconPosition: "left"}}
        loading={isLoading}
        // onfilteredFeedbackselect={handlefilteredFeedbackselect}
        onSearchChange={e => handleSeach(e)}
        value={searchValue}
      />
      <Card.Group>{FeedbackListToRender}</Card.Group>
    </Container>
  );
}

export default FeedbackList;
