import React, { useState } from "react";

import {Container} from "semantic-ui-react";

import {Form} from "semantic-ui-react";
import UserForm from "../user-component/UserForm";
import FeedbackForm from "../feedback-component/FeedbackForm";

function MainFormContainer() {
  const [allValues, setAllValues] = useState({
    step: 1,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    age: "",
    country: "",
    interests: [],
    about: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    //just show the success page (step 4 )
    // this.setState((prevState) => {
    //   return {
    //     ...prevState,
    //     step: 4,
    //   };
    // });
  };

  const handleChange = (event, {name, value, checked, type}) => {
    setAllValues((prevState) => {
      return {
        ...prevState,
        [name]: value,
      };
    });
  };

  const next = () => {
    setAllValues((prevState) => {
      return {
        ...allValues,
        step: prevState.step + 1,
      };
    });
    // console.log('steeep', this.state.step)
    // setStep(this.state.step + 1);
  };

  const prev = () => {
    setAllValues((prevState) => {
      return {
        ...allValues,
        step: prevState.step - 1,
      };
    });
    // setStep(this.state.step  - 1);
  };

  const [step, setStep] = useState(1);

  // const {firstName, lastName, email, gender, age, country, interests, about} =
  //   this.state;
  // const values = {
  //   firstName,
  //   lastName,
  //   email,
  //   gender,
  //   age,
  //   country,
  //   interests,
  //   about,
  // };

  return (
    <Container textAlign='left'>
      <Form onSubmit={handleSubmit}>
        <Step
          step={step}
          values={allValues}
          handleChange={handleChange}
          next={next}
          prev={prev}
        />
      </Form>
    </Container>
  );
}

const Step = ({step, values, handleChange, next, prev}) => {
  switch (step) {
    case 1:
      return (
        <UserForm values={values} handleChange={handleChange} next={next} />
      );
    case 2:
      return (
        <FeedbackForm
          values={values}
          handleChange={handleChange}
          next={next}
          prev={prev}
        />
      );
    // case 4:
    // return <Success values={values}
    // />
    default:
      return null;
  }
};

export default MainFormContainer
